mapper.insert:
INSERT INTO deliveryList
(address, priceTotal, detail, userId, status, deliveryId)
VALUES(
    #{address}
    , #{priceTotal}
    , #{detail}
    , #{userId}
    , #{status}
    , #{deliveryId}
)

mapper.update:
UPDATE deliveryList
SET
    address=#{address}
    , status=#{status}
WHERE deliveryId=#{deliveryId}

mapper.insert:
INSERT INTO deliveryAddressList
(
    userId
    , name
    , postcode
    , address
    , detailAddress
    , extraAddress
    , phon
    , defaultYn
)
VALUES(
    #{userId}
    , #{name}
    , #{postcode}
    , #{address}
    , #{detailAddress}
    , #{extraAddress}
    , #{phon}
    , #{defaultYn}
);

mapper.update:
UPDATE deliveryAddressList
SET
    name=#{name}
    , postcode=#{postcode}
    , address=#{address}
    , detailAddress=#{detailAddress}
    , extraAddress=#{extraAddress}
    , phon=#{phon}
    , defaultYn=#{defaultYn}
WHERE deliveryAddressId=#{deliveryAddressId}

UPDATE deliveryAddressList
SET defaultYn = 'N'
WHERE userId=#{userId}
